jQuery("textarea").each(function(){var e,t,n,c=jQuery(this),o=(c.parent(),c.closest(".form-element-textarea").find(".bbcode-bar"));c.on("mousedown",function(e){n=(new Date).getTime()}),c.on("mouseleave",function(e){n=0}),c.on("mouseup",function(i){e=i.clientX,t=i.clientY,(new Date).getTime()<n+300?o.removeClass("bbcode-active"):window.matchMedia("(min-width: 769px)").matches&&c.select(function(){o.css({left:e-150+"px",top:t+20+"px"}).addClass("bbcode-active")})}),c.on("contextmenu",function(c){if((new Date).getTime()>=n+300)return window.matchMedia("(min-width: 769px)").matches&&o.css({left:e-150+"px",top:t+20+"px"}).addClass("bbcode-active"),!1;o.removeClass("bbcode-active")}),o.find('[class*="hide-modal"], [onclick*="insertbbcode"]').each(function(){jQuery(this).on("click",function(){(new Date).getTime()<n+300?o.removeClass("bbcode-active"):c.select(function(){o.removeClass("bbcode-active")})})})});
